pub struct Transfer<B> { buffer: B, // 注記：追加しました serial: Serial1,
} impl<B> Transfer<B> { /// 転送が完了するまでブロックし、バッファを返します。 /// 注記：戻り値が変わっています pub fn wait(self) -> (B, Serial1) { // 転送が完了するまでビジーウェイトします while !self.is_done() {} (self.buffer, self.serial) } // ..
